<script>

import AddCharge from "../pages/content/SmsCharge/AddCharge";
import OneTimePayment from "../pages/content/SmsCharge/OneTimePayment";
import instance from "../../axios";

export default {
    name: "Sidebar.vue",
    props: ['charge'],
    components:{
        AddCharge, OneTimePayment
    },
    data(){
        return {
            isShowAddFundModel: false,
            isShowOneTimeFundModel: false,
            isSidebar: false,
        }
    },
    methods: {
      setRoute(name){
        localStorage.setItem('rc_currpage', name);
        this.$emit('update', name);
      },
        initJS(){
            $('.nav_ul .main_li .main_link').click(function(){
                if ($(this).parent().hasClass('active_li')) {
                    $(this).parent().removeClass('active_li');
                    $(this).next().slideUp();
                    $(this).find('.fa').removeClass('fa-angle-up');
                    $(this).find('.fa').addClass('fa-angle-down');
                }else{
                    $('.nav_ul .main_li .main_link').parent().removeClass('active_li');
                    $('.nav_ul .main_li .main_link').next().slideUp();
                    $('.nav_ul .main_li .main_link').find('.fa').removeClass('fa-angle-up');
                    $('.nav_ul .main_li .main_link').find('.fa').addClass('fa-angle-down');
                    $(this).parent().addClass('active_li');
                    $(this).next().slideDown();
                    $(this).find('.fa').removeClass('fa-angle-down');
                    $(this).find('.fa').addClass('fa-angle-up');
                }
            });
            $('.sub_menu li').click(function(){
                if ($(this).hasClass('active_li_sub')) {
                    $(this).removeClass('active_li_sub');
                }else{
                    $('.sub_menu li').removeClass('active_li_sub');
                    // $(this).addClass('active_li_sub');
                }
            });

            $('.time_ul li').click(function(){
                if ($(this).hasClass('active_li')) {
                    $(this).removeClass('active_li');
                }else{
                    $('.time_ul li').removeClass('active_li');
                    // $(this).addClass('active_li');
                }
            });
            $('.page_ul .page_li').click(function(){
                if ($(this).hasClass('active_li')) {
                    $(this).removeClass('active_li');
                }else{
                    $('.page_ul .page_li').removeClass('active_li');
                    // $(this).addClass('active_li');
                }
            });
            $('.page_ul .page_li').click(function(){
                if ($(this).hasClass('active_li')) {
                    $(this).removeClass('active_li');
                }else{
                    $('.page_ul .page_li').removeClass('active_li');
                    // $(this).addClass('active_li');
                }
            });
        },
        openModel(type){
            this.$emit('update', '', 'fund');
            // this.$refs.charge.openM(type);
        },
        closeModel(type){
            if(type == 'fund'){
                this.isShowAddFundModel = false;
            }else if(type == 'onetime'){
                this.isShowOneTimeFundModel = false;
            }else if(type == 'openonetimemodal'){
                this.isShowAddFundModel = false;
                this.isShowOneTimeFundModel = true;
            }else if(type == 'backcharge'){
                this.isShowAddFundModel = true;
                this.isShowOneTimeFundModel = false;
            }
        },
    },
  mounted() {
      this.initJS();
  }
}
</script>

