<script>
import instance from "../../../axios";
import moment from 'moment';
import Pagination from "../../Shopify/Pagination";
import AddCharge from "../../pages/content/SmsCharge/AddCharge";
import OneTimePayment from "../../pages/content/SmsCharge/OneTimePayment";
export default {
    name: "Dashboard",
    components: {
        Pagination, AddCharge, OneTimePayment
    },
     data(){
        return{
            isLoading: true,
            isShowAddFundModel: false,
            isShowOneTimeFundModel: false,
            data: [],
            analytics: [],
            latestMessages: [],
            shop: [],
            charge: [],
            tab: 'email',
            filter:{
                chartType: 'all',
                filterType: 'all_time',
                dateRange: {
                    startDate: '',
                    endDate: '',
                }
            }
        }
    },
    methods:{
        changePageData(url){
            this.getData(url);
        },
        async getData(url = 'dashboard'){
            let base = this;
            this.isLoading = true;
            await axios.post(url, {data: this.filter})
                .then(res => {
                    let data = res.data.data;
                    base.latestMessages = data.latestMessages;
                    base.analytics = data.analytics.all;
                    base.shop = data.shop;
                    base.data = data;
                    base.charge = res.data.data.charge;
            })
            .catch(err => {
                console.log(err);
            })
            .finally(res => {
                base.isLoading = false;
            });
        },
        filterData(days){
            this.filter.filterType = days;
            if(days == 'all_time'){
                this.filter.dateRange.startDate = '';
                this.filter.dateRange.endDate = '';
            }else{
                this.filter.dateRange.startDate = moment().subtract(days - 1, 'd').format('YYYY-MM-DD');
                this.filter.dateRange.endDate = moment().format('YYYY-MM-DD');
            }
            this.getData();
        },
        closeDModel(type){
            if(type == 'fund'){
                this.isShowAddFundModel = false;
            }else if(type == 'onetime'){
                this.isShowOneTimeFundModel = false;
            }else if(type == 'openonetimemodal'){
                this.isShowAddFundModel = false;
                this.isShowOneTimeFundModel = true;
            }else if(type == 'backcharge'){
                this.isShowAddFundModel = true;
                this.isShowOneTimeFundModel = false;
            }
        },
        openModel(type){
            this.$emit('update', type);
            // this.$refs.charge.openM(type);
        },
    },
    mounted(){
        this.getData('dashboard');
    },
}
</script>

