<script>
import instance from "../../../../axios";
import SubscribeGrowth from "../charts/SubscribeGrowth";
import Pagination from "../../../Shopify/Pagination";

export default {
    name: "EmailAnalytics",
    components: {
        SubscribeGrowth, Pagination
    },
    data(){
        return{
            isLoading: true,
            analytics: [],
            latestMessages: [],
            shop: [],
            subscribeGrowthChartData: [{
                name: 'Subscriber',
                data: [1,2,3,4,5,6,7,8]
            }],
            filter: {
                chartType: 'email',
                dateRange: []
            },
            filterDates:{},
        }
    },
    methods:{
        changePageData(url){
            this.getData(url);
        },
        async getData(url = 'analytics'){
            let base = this;
            await axios.post(url, {data: this.filter})
                .then(res => {
                    let data = res.data.data;
                    base.latestMessages = data.latestMessages;

                    if(base.isLoading){
                        base.analytics = data.analytics;
                        base.shop = data.shop;
                        base.subscribeGrowthChartData[0].data = data.analytics.charts.subscriber.value;
                        base.filterDates['subscriber'] = data.analytics.charts.subscriber.keys;
                    }
                })
                .catch(err => {
                    console.log(err);
                })
                .finally(res => {
                    base.isLoading = false;
                });
        },
    },
    mounted() {
        this.getData('analytics');
    },
    filters:{
        filterMessageSource(reminder_type, automation_type){
            return reminder_type;
        }
    }
}
</script>

