<script>
    let app = (() => {
        function updateSlider(element) {
            if (element) {
                let parent = element.parentElement,
                    lastValue = parent.getAttribute('data-slider-value');

                if (lastValue === element.value) {
                    return; // No value change, no need to update then
                }

                if(typeof allPlans != 'undefined' && allPlans.length > 0){
                    let currPlan = allPlans[element.value - 1];

                    if(typeof currPlan != 'undefined'){
                        let upto = (currPlan.upto == 'unlimited') ? 'Unlimited' : '$' + currPlan.upto;
                        let perM = currPlan.name.replace('/month', '');
                        $('.up-sales').html(upto);
                        $('.per-month-price').html(perM);
                    }
                }


                parent.setAttribute('data-slider-value', element.value);
                let $thumb = parent.querySelector('.range-slider__thumb_2'),
                    $bar = parent.querySelector('.range-slider__bar_2'),

                    pct = element.value / 11 * 100;
                    $thumb.style.bottom = `calc(${pct}% - ${$thumb.clientHeight}px)`;
                    $bar.style.height = `calc(${pct}% - ${$thumb.clientHeight}px)`;
                    $thumb.textContent = `${element.value}%`;
            }
        }
        return {
            updateSlider: updateSlider
        };
    })();

    (function initAndSetupTheSliders() {
        const inputs = [].slice.call(document.querySelectorAll('.range-slider_2 input'));
        inputs.forEach(input => input.setAttribute('value', 0));
        inputs.forEach(input => app.updateSlider(input));
        // Cross-browser support where value changes instantly as you drag the handle, therefore two event types.
        inputs.forEach(input => input.addEventListener('input', element => app.updateSlider(input)));
        inputs.forEach(input => input.addEventListener('change', element => app.updateSlider(input)));
    })();
    var allPlans=[];
    $( document ).ready(function() {
        $.ajax({
            url: "api/get-plans",
            cache: false,
            success: function(res){
                allPlans = res.data.plans;

                $('#planRange').attr('max', allPlans.length);
            }
        });
    });
</script>
